package exercises;

public class CheckigIFs {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int firstNumber = 16;
		int secondNumber = 20;
		int thirdNumber = 12;
		if (firstNumber > secondNumber && firstNumber > thirdNumber) {
			System.out.println("Greater is first Number");
		} else if (secondNumber > firstNumber && secondNumber > thirdNumber)
			System.out.println("Greater is Second Number");
		else
			System.out.println("Greater is Third Number");
	}

}
